class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)

        ans = 0
        count_1 = 0
        for i in range(n - 1):
            if s[i] == "1":
                count_1 += 1
            if s[i] == "1" and s[i + 1] == "0":
                ans += count_1

        return ans
