constexpr int MAX = 100010;

int a[MAX];

class Solution {
public:
    int maxOperations(string s) {
      int n = s.length(), m = 0;
      for (char ch : s) {
        int x = ch - '0';
        if (x == 1) {
          a[m++] = x;
        } else if (m == 0 || a[m - 1] != 0) {
          a[m++] = x;
        }
      }
      int cur = 0, ret = 0;
      for (int i = m - 1; i >= 0; --i) {
        if (a[i]) {
          ret += cur;
        } else {
          ++cur;
        }
      }
      return ret;
    }
};
