class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
        int count = 0;
        int noof0 = 0;
        int ans = 0;
        for (int i = 0; i < s.length(); i++) {
            if (s[i] == '0') {
                noof0 = 1;
            } else {
                if (noof0) ans += count;
                noof0 = 0;
                count++;
            }
        }
        if(noof0 ==1 )ans = ans + count;

        return ans;
    }
};
