public class Solution {
    public int MaxOperations(string s) {
        var ops = 0;
        var zeros = 0;
        var preZero = false;
        for (var i = s.Length - 1; i >= 0; i--) {
            if (s[i] == '0' && !preZero) {
                zeros++;
                preZero = true;
            }
            else if (s[i] == '1') {
                preZero = false;
                ops += zeros;
            }
        }

        return ops;
    }
}
