class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int start = 0, end = n-1;
        while(start < n && s[start] == '0') start++;
        while(end >= 0 && s[end] == '1') end--;
        if(start == n || end == -1) return 0;
        vector<int> cnt;
        int c = 0;
        for(int i = start; i <= end; ++i){
            if(s[i] == '0'){
                if(c) cnt.push_back(c);
                c = 0;
            }
            else c++;
        }
        if(c) cnt.push_back(c);
        int mul = cnt.size(); 
        int ans = 0;
        for(auto i : cnt){
            ans = ans + mul*i;
            mul--;
        }
        return ans;
    }
};
