class Solution {
public:
  int maxOperations(string s) {
    int numOnes = 0;
    int tot = 0;
    int n = s.size();
    for (int i = 0; i < n - 1; ++i) {
      if (s[i] == '1') {
        numOnes++;
        if (s[i + 1] == '0') {
          tot += numOnes;
        }
      }
    }
    return tot;
  }
};
