class Solution:
    def maxOperations(self, s: str) -> int:
        c1 = 0
        ans = 0
        i, n = 0, len(s)
        while i < n:
            c = s[i]
            j = i
            if c == "1":
                c1 += 1
            else:
                ans += c1
                while j + 1 < n and s[j + 1] == "0":
                    j += 1
            i = j + 1
        return ans
