class Solution {
public:
    int maxOperations(string s) {
        int ones_count = 0;
        int answer = 0;
        for (int i = 0; i < s.size(); i++)
        {
            if (s[i] == '1')
            {
                ones_count++;
                continue;
            }
            if (s[i] == '0' && (i == s.size() - 1 || s[i+1] == '1'))
                answer += ones_count;
        }
        return answer;
    }
};
