class Solution {
    public int maxOperations(String s) {
        int ans = 0;
        int z=0;
        int o=0;
        for(int i=0;i<s.length();i++){
            char ch = s.charAt(i);
            if(ch == '0')
            z++;
            else
            o++;

            if(ch == '0'){
                int j = i;
                ans+=o;
                while(j<s.length() && s.charAt(j) == '0'){
                    j++;
                }
                i=j-1;
            }
        }
        return ans;
    }
}
