class Solution {
    public int maxOperations(String s) {
        int res = 0;
        int n = s.length();
        int j = n - 1;
        while (j >= 0 && s.charAt(j) == '0') {
            j--;
        }
        int a = 0;
        boolean f = false;
        for (int i = 0; i <= j; i++) {
            if (s.charAt(i) == '1') {
                if (f) {
                    res += a;
                    f = false;
                }
                a++;
            } else if (a > 0 && s.charAt(i) == '0') {
                f = true;
            }
        }
        if (j < n - 1) {
            res += a;
        }
        return res;
    }

    public static void main(String[] args) {
        Solution s = new Solution();
        s.maxOperations("1001101");
    }
}
