class Solution:
    def maxOperations(self, s: str) -> int:
        tr = 0
        n = len(s)
        last = -1
        ret = 0
        lz = -1
        for i in range(n):
            c = s[i]

            if c == '0':
                lz = i

            if c == '1':
                if i - last > 1:
                    ret += tr
                tr += 1
                last = i

        if s[-1] == '0':
            ret += tr

        return ret
