class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0, res = 0;
        int flag = 1;
        for(auto it : s){
            if(it=='1'){
                cnt++;
                flag = 1;
            }
            else {
                if(flag ) res+=cnt;
                flag = false;
            }
        }
        return res;
    }
};
