class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int n = s.length();
        vector<pair<int,int>> v;
        for(int i=0; i<n; i++){
            char num = s[i];
            int cnt = 0;
            while(i < n && s[i] == num){
                cnt++;
                i++;
            }
            v.push_back({cnt,num-'0'});
            i--;
        }
        int found = 0;
        for(int i=0; i<v.size(); i++){
            if(v[i].second == 0){
                if(i > 0){
                    ans += found;
                }
            }
            else{
                found += v[i].first;
            }
        }
        return ans;
    }
};
