class Solution {
public:
    int maxOperations(string s) {
        vector<int> arr = processBits(s);
        int res = 0;
        int cnt = 0;

        for (int bit : arr) {
            if (bit == 1) {
                cnt += 1;
            } else {
                res += cnt;
            }
        }

        return res;
    }

private:
    vector<int> processBits(const string& s) {
        vector<int> arr;
        bool one = false;

        for (char ch : s) {
            if (ch == '1') {
                arr.push_back(1);
                one = true;
            } else if (ch == '0' && one) {
                arr.push_back(0);
                one = false;
            }
        }

        return arr;
    }
};
