class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();

        bool flag = false;
        int ans = 0;
        int cnt = 0;
        for(int i = n-1; i >= 0; --i){
            if(s[i] == '1'){
                flag = false;
                ans += cnt;
            }
            else if(!flag){
                flag = true;
                cnt ++;
            }
        }
        return ans;
    }
};
