class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int k=0;
        bool flag=false;
        vector<int>v;
        for(char c : s){
            if(c=='1')
            {
                v.push_back(1);
                flag=true;
            }
           if(c=='0' && flag){
                v.push_back(0);
                flag=false;
            }
        }
        for(int i=0;i<v.size();i++){
            if(v[i]==1){
                k++;
            }
            else ans=ans+k;
        }
        return ans;
    }
};
