class Solution {
public:
    int maxOperations(string s) {
        int count=0;
        int maxOne=0;
        bool flag=false;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'&&flag){
                count+=maxOne;
                flag=false;
                maxOne++;
            }else if(s[i]=='1'){
                maxOne++;
            }else if(s[i]=='0'){
                flag=true;
            }
        }
        if(flag){
            count+=maxOne;
        }
        return count;
    }
};
