class Solution {
public:
    int maxOperations(string s) {
        int ones =0;
        int ans = 0;
        for(int i = 0;i<s.size();i++){
            if(s[i]=='1')++ones;
            else {
                ans += ones;
                while(i!=s.size() && s[i] =='0')++i;
                if(i<s.size())++ones;
            }
        }
        return ans;
    }
};
