class Solution {
public:
    int maxOperations(string s) {
        int n=s.length();
        int curr=n-1,ans=0,ps=0;
        for(int i=n-1;i>=0;i--){
            if(s[i]=='1'){
                ans+=ps;
            }
            else if(i==n-1 or s[i+1]=='1') ps++;
        }
        return ans;
    }
};
