class Solution { 
public: 
    int maxOperations(string s) { 
        int operations = 0;
        int consecutiveOnes = 0;
        bool seenOne = false; 
        vector<int> segments; 
        for(char c : s) { 
            if(c == '1') { 
                segments.push_back(1); 
                seenOne = true; 
            } 
            if(c == '0' && seenOne) { 
                segments.push_back(0); 
                seenOne = false; 
            } 
        } 
        for(int segment : segments) { 
            if(segment == 1) { 
                consecutiveOnes++; 
            } else { 
                operations += consecutiveOnes; 
            } 
        } 

        return operations; 
    } 
};
