class Solution {
public:
    int maxOperations(string s) {
        int res = 0, n = s.size();
        string p;
        for(int i = 0; i < n; i ++) {
            if(i > 0 && s[i] == '0' && s[i - 1] == '0') continue;
            p.push_back(s[i]);
        }
        //cout << p << endl;
        int a = 0, b = 0;
        for(int i = 0 ;i < p.size(); i ++) {
            if(p[i] == '1') a ++;
            else res += a;
        }
        return res;
    }
};
