class Solution {
public:
    int maxOperations(string s) 
    {
        int n=s.size();
        int ans=0;
        int ones=0;
        if(s[0]=='1')
            ones++;
        for(int i=1; i<n; i++)
        {
            if(s[i]=='0')
            {
                if(s[i-1]=='1')
                    ans+=ones;
                else
                    continue;
            }
            else
            {
                ones++;
            }
        }

        return ans;
    }
};
