class Solution {
public:
    int maxOperations(string s) {
        string a=s;
        int ans=0;
        int  n=s.size();
        int start=0;
        int prev=0;
        while(start<n){
            char c=s[start];
            if(c=='1'){
                int one=0;
                while(start<n and a[start]=='1'){
                    start++;
                    one++;
                }
                int zero=0;
                 while(start<n and a[start]=='0'){
                    start++;
                    zero++;
                }
                int add=((int)prev+one);
                if(zero==0){
                    add=0;
                }
                prev=prev+one;
                ans=(int)ans+add;


            }
            else{
                start++;

            }

        }
        return ans;

    }
};
