class Solution {
public:
    int maxOperations(string s) {

         int ans = 0;
    int onesCount = 0;
    bool Previous = false;
    vector<int> bits;

    for (char bit : s) {
        if (bit == '1') {
            bits.push_back(1);
            Previous = true;
        } else if (bit == '0' && Previous) {
            bits.push_back(0);
            Previous = false;
        }
    }

    for (int bit : bits) {
        if (bit == 1) {
            onesCount++;
        } else {
            ans += onesCount;
        }
    }

    return ans;

    }
};
