class Solution:
    def maxOperations(self, s: str) -> int:
        index=0
        total=0
        curr=0
        while index<len(s):
            while index<len(s) and s[index]=="1":
                curr+=1
                index+=1
            if index>=len(s):
                break
            while index<len(s) and  s[index]=="0":
                index+=1
            total+=curr
        return total
