class Solution {
public:
    int maxOperations(string s) {
        bool prevOne = false;
        int count = 0;
        int ones = 0;
        for (int i = 0; i < s.size(); i++) {
            if (s[i] == '1') {
                ones++;
                continue;
            }

            if (i+1 < s.size() && s[i+1] == '0') {
                continue;
            }

            count += ones;
        }
        return count;
    }
};
