class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, op = 0;
        bool add = true;

        for(int i = 0; i < s.size(); i++) {
            if(s[i] == '1') {
                op++;
                add = true;
            } else {
                if(add) ans += op;
                add = false;
            }
        }

        return ans;
    }
};
