#include <vector>
#include <string>
using namespace std;

class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int p = 0;
        bool has_one = false;
        vector<int> bits;

        // Traverse the string to populate the bits vector
        for (char ch : s) {  // Changed 'char char' to 'char ch'
            if (ch == '1') {
                bits.push_back(1);
                has_one = true;
            } else if (ch == '0' && has_one) {
                bits.push_back(0);
                has_one = false;
            }
        }

        // Calculate the number of operations
        for (int bit : bits) {
            if (bit == 1) {
                p += 1;
            } else {
                ans += p;
            }
        }

        return ans;
    }
};
