class Solution {
public:
    int maxOperations(string s) {
        reverse(s.begin(),s.end());
        int cnt=0;
        int is=0,p=-1;
        for(auto i:s) {
            if(i == '0' && p!=0) is++;
            else if(i=='1') cnt+=is;
            p = i-'0';
            // cout << is <<" ";
        } 
        // cout << endl;
        return cnt;
    }
};
