class Solution {
public:
    int maxOperations(string s) {
        int ones = 0;
        int ans = 0;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                ones++;
            }else{
                if(s[i]=='0'&&(i+1==s.size() || s[i+1]!='0')){
                    ans+= ones;
                }
            }
        }
        return ans;
    }
};
