class Solution {
public:
    int maxOperations(string s) {
        int cnt1=0, ans=0;
        if(s[0]=='1') cnt1++;
        for(int i=1;i<s.length();i++){
            if(s[i]=='0' && s[i-1]!='0'){
                ans+=cnt1;
            }
            else if(s[i]=='1') cnt1++;
        }
        return ans;
    }
};
