class Solution:
    def maxOperations(self, s: str) -> int:
        res = 0
        currOnes = 0
        for i, c in enumerate(s):
            if c == '1':
                currOnes += 1
            else:
                if i > 0 and s[i - 1] == '1':
                    res += currOnes

        return res
