class Solution {
public:
    int maxOperations(string s) {
        int n=s.length();
        int cnt=0, ans=0;
        bool move=0;
        vector<int>val;
        for(auto it:s){
            if(it=='1'){
                val.push_back(1);
                move=1;
            }
            else if(it=='0' && move){
                val.push_back(0);
                move=0;
            }
        }
        for(auto it:val){
            if(it==1) cnt++;
            else ans+=cnt;
        }
        return ans;

    }
};
