impl Solution {
    pub fn max_operations(s: String) -> i32 {
        s.into_bytes().into_iter().chain(std::iter::once(b'1')).fold((0, false, 0), |(prev_ones, has_zero, ans), c| {
            if c == b'0' {
                (prev_ones, true, ans)
            }
            else {
                if has_zero {
                    (prev_ones + 1, false, ans + prev_ones)
                }
                else {
                    (prev_ones + 1, false, ans)
                }
            }
        }).2
    }
}
