class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)

        answer = 0
        one = 0
        i = 0
        while i < n:
            while i < n and s[i] == "1":
                one += 1
                i += 1
            if i == n:
                break
            while i < n and s[i] == "0":
                i += 1
            answer += one
        return answer
