#define F first
#define For(i, a, b) for (int i = (a); i < (b); i++)
#define S second
#define pb push_back
#define all(x) (x).begin(), (x).end()
#define sz(x) ((int)(x).size())

class Solution
{
public:
    int maxOperations(string st)
    {
        int res = 0, series = 0;
        bool one = true;
        For(i, 0, sz(st))
        {
            if (st[i] == '1')
            {
                ++series;
                one = true;
            }
            else
            {
                if (one)
                    res += series;
                one = false;
            }
        }
        return res;
    }
};
