class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int last = -1, cnt = 0, res = 0;
        for (int i = 0; i < n; i++)
        {
            if (s[i] == '1')
            {
                if (last + 1 != i)
                {
                    res += cnt;
                }
                last = i;
                cnt++;
            }
        }

        if (last != n - 1)
        {
            res += cnt;
        }

        return res;
    }
};
