class Solution {
public:
    int maxOperations(string s) {
        int res = 0, ones=0;
        bool move=false;
        for (char c: s) {
            if (c=='1') {
                ++ones;
                move=false;
            } else {
                if (!move) res+=ones;
                move=true;
            }
        }
        return res;
    }
};
