class Solution {
public:
    int maxOperations(string s) {
        int cnt_one = 0;
        int ans = 0;
        int n = s.size();
        for(int i = 0; i < n;) {
            char c = s[i];
            if (c == '1') {
                cnt_one++;
                i++;
            } else {
                ans += cnt_one;
                while(i < n && s[i] == '0')
                    i++;
            }
        }
        return ans;
    }
};
