class Solution:
    def maxOperations(self, s: str) -> int:
        r = 0
        pre = 0
        n = len(s)
        i = 0
        while i < n:
            if (i == 0 or s[i - 1] == '0') and s[i] == '1':
                cur = 0
                while i < n and s[i] == '1':
                    cur += 1
                    i += 1
                r += pre
                pre += cur
            i += 1
        if s[-1] == '0':
            r += pre
        return r
