class Solution {
public:
    int maxOperations(string s) {
        //先移动左边，再移动右边，再移动左边
        int n = s.length();
        int res = 0;
        int cnt = 0;//统计有多少个1
        for(int i = 0; i < n - 1; i++){
            if(s[i] == '1'){
                if(s[i + 1] == '0'){//可以移动
                    res += cnt + 1;//加cnt是因为前面的1也可以跟着移动
                }
                cnt++;
            }
        }
        return res;
    }
};
