class Solution {
public:
    int maxOperations(string s) {
        int size = s.size();
        vector<int> pos;
        for(int i = 0; i < size; i++){
            if(s[i] == '1'){
                pos.emplace_back(i);
            }
        }
        pos.emplace_back(size);
        int res = 0;
        int pos_size = pos.size();
        int pre_step = 0;
        size--;
        for(int i = pos_size-2; i >= 0; i--, size--){
            if(size != pos[i]){
                if(pos[i] < pos[i+1]-1){
                    cout << "1: pos[i]: " << pos[i] << " pre_step: " << pre_step <<endl;
                    res += (pre_step +1);
                    pre_step++;
                }
                else{
                    cout << "2: pos[i]: " << pos[i] << " pre_step: " << pre_step <<endl;
                    res += pre_step;
                }
            }
        }
        return res;
    }
};
