func maxOperations(s string) int {
    count:=0
    n:=len(s)
    ans:=0
    for i:=0;i<n;i++{
        if s[i]=='0' {
            ans+=count
            for i<n && s[i]=='0' {
                i++
            }
            i--
        }else {
            count++
        }
    }
    return ans
}
