class Solution:
    def maxOperations(self, s: str) -> int:
        seg=[]
        cc=0
        for i in s:
            if i=='1':
                cc+=1
            else:
                if cc>0:
                    seg.append(cc)
                cc=0
        #print(seg)
        ans=0
        cc=0
        for i in seg:
            cc+=i
            ans+=cc
        return ans
