class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int cnt = 0;
        bool flag = false;
        for (char c: s) {
            if (c == '1') {
                cnt++;
                flag = true;
            }
            else {
                if (flag) ans += cnt;
                flag = false;
            }
        }
        return ans;
    }
};
