class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        vector<int> vec(n+1);
        int index = 0;
        vec[index] = 0;
        index++;

        vector<int> arr(n+1);
        if(s[0] == '1')
        {
            arr[0] = 1;
        }
        else
        {
            arr[0] = 0;
        }
        for(int i = 1;i<n;++i)
        {
            if(s[i] == '0')
            {
                arr[i] = arr[i-1];
            }
            else
            {
                arr[i] = arr[i-1]+1;
            }
        }
        for(int i = 1;i<n;++i)
        {
            if(s[i] == '1')
            {
                if(s[i -1] != '1')
                {
                    vec[index] = arr[i]-1+vec[index-1];
                    index++;
                }
                else
                {
                    vec[index] = vec[index-1];
                    index++;
                }
            }
            else if(s[i] == '0' && i == n-1)
            {
                    vec[index] = arr[i]+vec[index-1];
                    index++;
            }
        }
        return vec[index-1];
    }
};
