def remove_consecutive_zeros(s: str) -> str:
    result = []
    zero_found = False

    for char in s:
        if char == '1':
            result.append(char)
            zero_found = False
        elif char == '0':
            if not zero_found:
                result.append(char)
                zero_found = True

    return ''.join(result)

class Solution:
    def maxOperations(self, s: str) -> int:
        s = remove_consecutive_zeros(s)

        operations = 0
        coefficient = 0
        count_one = 0

        for char in reversed(s):
            if char == '1':
                if coefficient > 0:
                    operations += coefficient
                count_one += 1
            elif char == '0':
                coefficient += 1

        return operations
