class Solution {
public:
    int maxOperations(string s) {
        int res = 0;
        int noZeroes;
        int buffer = 1;
        for (int i = s.size()-2; i >= 0; i--){
            if (s[i] == '1' && s[i+1] == '0') {
                res+= buffer;
                buffer++;
            }
            else if (s[i] == '1' && s[i+1] == '1' && res > 0){
                res += buffer-1;
            }
        }
        return res;
    }
};
