class Solution {
public:
    int maxOperations(string s) {
        int ones = 0;
        int count = 0;
        for (int i = 0; i <= s.size(); ++i) {
            if (i == s.size() || s[i] == '1') {
                if (i != 0 && s[i-1] == '0') 
                    count += ones;
                ++ones;
            }
        }
        return count;
    }
};
