/**
 * @param {string} s
 * @return {number}
 */
var maxOperations = function(s) {
    let cnt = 0;
    let ans = 0;
    let flag_one = false;
    let bits_arr = [];

    for (let i = 0; i < s.length; i++) {
        let c = s[i];
        if (c === '1') {
            flag_one = true;
            bits_arr.push(1);
        } else if (c === '0' && flag_one) {
            flag_one = false;
            bits_arr.push(0);
        }
    }

    let i = 0;
    while (i < bits_arr.length) {
        if (bits_arr[i] === 1) {
            ans++;
        } else {
            cnt += ans;
        }
        i++;
    }

    return cnt;
};
